                  <br />
                  <INPUT
                    label="TYPE"
                    name="type"
                    type="type"
                    required
                    onChange={handleChange}
                    value={values.type}
                  />

                  <br />
                  <INPUT
                    label="EMAIL"
                    name="email"
                    type="email"
                    required
                    onChange={handleChange}
                    value={values.email}
                  />
                  <br />
                  <INPUT
                    label="HOSPITAL CONTACT"
                    name="hospitalContact"
                    type="hospitalContact"
                    required
                    onChange={handleChange}
                    value={values.hospitalContact}
                  />
                  <br />
                  <INPUT
                    label="ADMIN CONTACT"
                    name="adminContact"
                    type="adminContact"
                    required
                    onChange={handleChange}
                    value={values.adminContact}
                  />

                  <br />
                  <INPUT
                    label="AMBULANCE NUMBER"
                    name="ambulanceNumber"
                    type="ambulanceNumber"
                    required
                    onChange={handleChange}
                    value={values.ambulanceNumber}
                  />
                  <br />

                </Stack>
                <Button
                  isLoading={loading}
                  loadingText="Uploading..."
                  bgGradient={primaryLoginC}
                  type="submit"
                  color="#fff">
                  UPLOAD FOR APPROVAL
                </Button>

              </Stack>
            </form>
          </>
        )}
      </Formik>
    </Box>
  );
};
